
Cocktail.create!([
  {cocktail_name: "Spicy Margarita", ingredient: "Makes 1 Drink\n\n3 slices of muddled red peppers\n2 parts Herradura Tequila\n3/4 parts Cointreau\n1/2 parts lime juice\n1/2 parts simple syrup\npinch of cilantro", direction: "1. Combine all ingredients in a shaker and then pour into a glass with ice.\n\n2. Garnish with a slice of red pepper.", recipe_link: "https://www.foodrepublic.com/recipes/spicy-margarita-recipe/", favorite: nil},
  {cocktail_name: "Blackberry Thyme Margaritas", ingredient: "Makes 2 Drinks\n\n18 blackberries, divided\n3 small sprigs fresh thyme\n1 cup crushed ice\n1/3 cup tequila\n0.25 cup simple syrup\nJuice of 1 lime\n1 tbsp. orange liqueur such as triple sec or Cointreau\n0.25 cup sparkling water", direction: "1. Place 16 of the blackberries in a food processor or blender with the thyme leaves.  Pulse to puree until smooth.\n\n2. Strain the blackberry puree through a fine mesh sieve into a small bowl, pressing on the solids to extract as much liquid as possible.\n\n3. Combine the blackberry puree, ice, tequila, simple syrup, lime juice, orange liqueur and sparkling water in a blender or food processor. Blend until smooth and well combined.\n\n4. Pour into two glasses and garnish with reserved blackberries and additional thyme, if desired.", recipe_link: "https://everydayannie.com/2011/06/08/blackberry-thyme-margaritas/", favorite: nil},
  {cocktail_name: "The Sterling Archer Bloody Mary", ingredient: "Makes 1 Drink\n\n3 oz vodka\n6 oz freshly squeezed tomato juice\n1 oz freshly squeezed lime juice\n0.5 teaspoon freshly grated horse radish\ndash of Worcestershire sauce\ndash of hot sauce\ndash of salt and pepper\n\nFor the garnish on a plastic sword:\n1 rib organic celery, with leafy bits still attached\n3 Extremadura olives (pitted and pimiento-ed ahead of time by your valet)\n3 caper berries", direction: "1. Shake ingredients, over ice, in a cocktail mixer.\n\n2. Pour mixture and ice into an imperial pint glass and garnish.\n\n\n\"For I am a sinner in the hands of an angry God. Bloody Mary full of vodka, blessed are you among cocktails. Pray for me now and at the hour of my death, which I hope is soon. Amen.\"", recipe_link: "https://archer.fandom.com/wiki/Bloody_Mary", favorite: nil},
  {cocktail_name: "Talisker Old Fashioned", ingredient: "1.5 oz Talisker whisky\n0.25 oz sugar syrup\n1 dash of bitters \nIce", direction: "1. Pour whisky, bitters and sugar syrup to a mixing glass. Add ice.\n\n2. Mix and serve over cube of ice", recipe_link: "https://www.thebar.com/en-gb/whisky/old-fashioned-cocktails-that-rock", favorite: nil},
  {cocktail_name: "Jalapeño Tequila Gimlet", ingredient: "Makes 8 drinks\n\n2 jalapeños, stemmed\n2 cups tequila blanco\n1 cup fresh lime juice\n0.5 cup agave syrup (nectar)", direction: "1. Grill, broil or roast jalapeños over a gas flame, turning frequently, until soft and charred. Stem and chop chiles.\n\n2. Transfer chiles and seeds, if using, to a jar. Add tequila and let sit for 1 hour. Strain mixture through a fine-mesh sieve into a medium pitcher; discard solids in sieve. \n\n3. Stir in lime juice, agave syrup, and 2 cups ice (to chill and dilute). Let stand, stirring occasionally, until ice is melted.\n\n4. Divide gimlet among chilled glasses.", recipe_link: "https://www.bonappetit.com/recipe/jalapeno-tequila-gimlet", favorite: nil},
  {cocktail_name: "Vegan White Russian", ingredient: "25 ml coffee liqueur\n25 ml vodka\n50 ml oat milk (barista edition)", direction: "1. Combine all ingredients in an ice-filled cocktail shaker.\n\n2. Shake and strain into a chilled glass", recipe_link: "https://amp.theguardian.com/food/2021/dec/12/easy-christmas-cocktails-mulled-gin-negroni-punch-vegan-white-russian", favorite: nil},
  {cocktail_name: "Sweet Liberty's Pina Colada", ingredient: "Makes 2 drinks\n\n3 cups crushed ice\n3 oz aged rum\n4 1-inch fresh pineapple chunks\n3 oz pineapple juice\n6 oz cream of coconut (see headnote)\n10 coffee beans\n2 oz Pedro Ximénez sherry\nMint sprig, for garnish (optional)\nFresh cherry, for garnish (optional)", direction: "1. Chill a tall glass.\n\n2. Combine the ice, rum, pineapple chunks and juice, cream of coconut and coffee beans in a blender; puree until smooth.\n\n3. Pour into the chilled glass; pour the sherry on top. Garnish with the mint sprig and/or cherry, if using.", recipe_link: "https://www.washingtonpost.com/recipes/sweet-libertys-pina-colada/15979/?itid=lk_inline_manual_8", favorite: nil},
  {cocktail_name: "Frozen Irish Coffee", ingredient: "Makes 2 drinks\n\n0.5 cup vanilla ice cream\n2 cups ice cubes\n4 ounces chilled coffee\n2 ounces brandy\n2 ounces coffee liqueur, such as Tia Maria\n0.25 teaspoon coffee grounds, for garnish (optional)", direction: "1. Combine the ice cream, ice cubes, coffee, brandy and coffee liqueur in a blender. Puree on HIGH until the ice is completely broken down and the mixture is the consistency of a milkshake.\n\n2. Divide between two highball glasses, then sprinkle a pinch of coffee grounds, if using, over the top of each. Reserve any leftovers for topping off.", recipe_link: "https://www.washingtonpost.com/recipes/frozen-irish-coffee/15769/?itid=lk_inline_manual_16", favorite: nil},
  {cocktail_name: "Homemade Eggnog", ingredient: "1 dozen eggs\n1.5 cups bourbon\n0.5 cup cognac\n1/3 cup dark rum\n1.5 cups sugar\n\n6 cups whole milk\n0.5 cup cream", direction: "1. Stir together the eggs, spirits and sugar.\n\n2. Pour mixture in an airtight container and refrigerate for three weeks.\n\n3. After three weeks, pour the base through a fine-mesh strainer to remove any egg solids.\n\n4. Mellow out the mixture with whole milk and cream.", recipe_link: "https://www.americastestkitchen.com/cooksillustrated/articles/4049-the-best-eggnog-is-old-eggnog?extcode=NSCIL14IG&utm_source=instagram&utm_medium=photo&utm_content=oldeggnog&utm_campaign=ciinstagram", favorite: nil},
  {cocktail_name: "Agave Margarita", ingredient: "Makes 1 Drink\n\n0.25 cup tequila blanco\n1 tablespoon agave syrup (nectar)\n0.25 cup fresh lime juice\nKosher salt\n2 lime slices or wedges", direction: "1. Pour some kosher salt into a small dish.\n\n2. Rub 1 lime slice over half the rim of an Old Fashioned glass (if you prefer your Margarita on the rocks) or a coupe glass (if you prefer it up).\n\n3. Dip rim of glass into salt.\n\n4.Combine tequila, agave syrup, and juice in a cocktail shaker; fill with ice and shake well. Strain into prepared glass.\n\n5. Garnish with second lime slice.", recipe_link: "https://www.bonappetit.com/recipe/agave-margarita", favorite: nil},
  {cocktail_name: "Eggnog", ingredient: "Makes 1 drink\n\n50 ml vanilla ice cream\n50 ml aged rum or whiskey\nnutmeg", direction: "1. Combine the ice cream and spirit in  a cocktail shaker and shake without ice.\n\n2. Pour into a chilled glass and grate nutmeg over the top.", recipe_link: "https://amp.theguardian.com/food/2021/dec/12/easy-christmas-cocktails-mulled-gin-negroni-punch-vegan-white-russian", favorite: nil},
  {cocktail_name: "The Macallan Smoked Oak Old Fashioned", ingredient: "50 ml The Macallan 12 Years Old\n15 ml maple syrup\nPinch of smoked salt", direction: "Add all the ingredients to an Old Fashioned glass and stir together with a small ice cube. Add more ice and a cherry to garnish.", recipe_link: "https://www.lifestyleasia.com/hk/food-drink/drinks/shake-and-stir-the-macallan-to-make-these-cocktails-at-home/", favorite: nil},
  {cocktail_name: "Gold Rush", ingredient: "2 oz bourbon\n0.75 oz honey syrup\n0.75 oz fresh lemon juice\n\nHoney Syrup:\n1 cup honey\n1/3 cup water", direction: "1. Combine all ingredients in a cocktail shaker filled with ice, and shake vigorously until the drink is sufficiently chilled.\n\n2. Strain into a chilled double Old Fashioned glass with a single large ice cube.", recipe_link: "https://whiskyadvocate.com/gold-rush-cocktail-recipe/", favorite: nil},
  {cocktail_name: "Dashiell Hammock (Old Fashioned)", ingredient: "1 oz Cotton & Reed Banana Hammock\n1 oz High Proof Rye Whiskey (we like Wild Turkey 101)\n~1 tsp Simple Syrup\n2 dashes Angostura Bitters\n2 dashes Orange Bitters", direction: "Build in a rocks glass, add ice, and stir well. Garnish with a lemon peel, being sure to express essential oils atop the drink.", recipe_link: "https://web.archive.org/web/20190517182515/https://www.cottonandreed.com/recipes/", favorite: nil},
  {cocktail_name: "Simba!", ingredient: "1.5 oz Cotton & Reed Banana Hammock\n0.5 oz Cotton & Reed Allspice Dram\n0.5 oz Simple Syrup\n0.75 oz Fresh Lime Juice\n.25 oz Funky, Overproof Rum, such as Wray & Nephew", direction: "Build in a shaking tin, add ice, and shake vigorously. Strain into a chilled cocktail glass.", recipe_link: "https://web.archive.org/web/20190517182515/https://www.cottonandreed.com/recipes/", favorite: nil},
  {cocktail_name: "Golden Highway", ingredient: "2 oz Cotton & Reed Mellow Gold\n0.25 oz Blackstrap & Turmeric Syrup*\n2 dashes Lemon Bitters\n\nBlackstrap & Turmeric Syrup:\nBring 1 cup of water to boil.\nAdd 2 tbsp peeled, diced turmeric, remove from the heat, and steep for 7 minutes.\nStrain and allow to cool.\nCombine with 1 cup Blackstrap Molasses and stir well to combine.", direction: "Build in rocks glass, add ice, and stir well. Garnish with a lemon peel, being sure to express the essential oils atop the drink.", recipe_link: "https://web.archive.org/web/20190517182515/https://www.cottonandreed.com/recipes/", favorite: nil},
  {cocktail_name: "Henhouse Sazerac", ingredient: "1 oz Cotton & Reed Mellow Gold\n1 oz Cognac (we like Hennessy VSOP)\n0.25 oz Simple Syrup\n4 dashes Peychaud's Bitters\nAbsinthe, to rinse the glass", direction: "Build in mixing glass, add ice, and stir well.\nStrain into a chilled, absinthe-rinsed rocks glass.\nGarnish with a lemon peel, being sure to express the essential oils atop the drink.", recipe_link: "https://web.archive.org/web/20190517182515/https://www.cottonandreed.com/recipes/", favorite: nil},
  {cocktail_name: "Dram Rickey", ingredient: "1.5 oz Cotton & Reed Allspice Dram\n0.75 oz lime\nsoda water", direction: "Build in Collins glass over ice, top with soda water, garnish with lime", recipe_link: "https://web.archive.org/web/20190517182515/https://www.cottonandreed.com/recipes/", favorite: nil},
  {cocktail_name: "White Lion", ingredient: "1.5 oz Cotton & Reed White Rum\n0.75 oz Lime\n0.5 oz Cotton & Reed Allspice Dram\nScant half ounce simple syrup", direction: "Build in shaker, shake, strain into chilled cocktail glass.", recipe_link: "https://web.archive.org/web/20190517182515/https://www.cottonandreed.com/recipes/", favorite: nil},
  {cocktail_name: "Redbeard", ingredient: "1.5 oz Cotton & Reed White Rum\n.5 oz Lime\n.25 oz Campari\nFever Tree Ginger Beer\nLime wheel", direction: "Build rum, lime juice, and Campari in a rocks or Collins glass.\nAdd ice, top with ginger beer and a wheel of lime.", recipe_link: "https://web.archive.org/web/20190517182515/https://www.cottonandreed.com/recipes/", favorite: nil},
  {cocktail_name: "Old Fashioned (Original)", ingredient: "2.25 (or 2) oz Whiskey\n3 dashes Aromatic Bitters\n0.25 oz Simple Syrup\n1 Orange Peel", direction: "Add bitters and syrup to the bottom of an old fashioned glass.\nSwirl the glass to combine.\nPack the glass with ice.\nPour whiskey over the ice.\nStir gently around the inside of the glass, making little to no noise, for 15-20 turns of the barspoon.\nGarnish with orange peel.", recipe_link: "https://recipes.prattstandard.com/recipe/246/", favorite: nil},
  {cocktail_name: "Cucumber Gimlet", ingredient: "1.5 oz gin\n1 oz lime juice, freshly squeezed\n1 oz simple syrup*\n4-5 slices of cucumber", direction: "Add cucumber, fresh lime juice and simple syrup to a shaker.\nMuddle with three ice cubes until cucumber is dissolved.\nAdd ice to shaker to fill halfway.\nShake gently and strain into a highball.", recipe_link: "https://www.hgtv.com/lifestyle/entertaining/easy-cocktails-you-can-make-at-home-pictures", favorite: nil},
  {cocktail_name: "The Godfather", ingredient: "2 oz scotch whisky\n0.5 oz amaretto", direction: "1. Stir the scotch and Amaretto well with cracked ice in a mixing glass. \n\n2. Strain over fresh ice into an Old Fashioned glass.", recipe_link: "https://www.esquire.com/food-drink/drinks/a30653311/best-godfather-drink-recipe/", favorite: nil},
  {cocktail_name: "The Macallan Copa", ingredient: "35 ml The Macallan 12 Years Old Fine Oak\n15 ml Manzanilla sherry\nSqueeze of fresh lemon\nTtonic water.", direction: "1. Build all the ingredients in a Spanish copa and garnish with a lemon wheel.", recipe_link: "https://www.lifestyleasia.com/hk/food-drink/drinks/shake-and-stir-the-macallan-to-make-these-cocktails-at-home/", favorite: nil},
  {cocktail_name: "Tickled Pink Gimlet", ingredient: "Makes 1 Drink\n\n2 oz Lillet Rose\n2 oz fresh grapefruit juice plus 1 grapefruit peel twist, for garnish\n1 oz gin ", direction: "1. Pour the Lillet Rose, grapefruit juice and gin into a cocktail shaker filled with ice.\n\n2. Shake until well chilled.\n\n3. Strain into a chilled coupe and garnish with a twist of grapefruit peel.", recipe_link: "https://www.foodnetwork.com/recipes/geoffrey-zakarian/tickled-pink-gimlet-5175798", favorite: nil},
  {cocktail_name: "Classic Gin Martini", ingredient: "2.5 oz Aviation American Gin\n0.5 oz Dry Vermouth\n1 dash Regan's No.6 Orange Bitters (or any bitters you have on hand)\nLemon twist for garnish", direction: "1. In a mixing glass or a cocktail shaker filled with ice, combine the gin and the vermouth.\n\n2. Shake well, then strain into a martini glass.\n\n3. Top with a dash of bitters.\nGarnish with a lemon twist and serve.", recipe_link: "https://www.hgtv.com/lifestyle/entertaining/easy-cocktails-you-can-make-at-home-pictures", favorite: nil},
  {cocktail_name: "Cantaloupe Margarita", ingredient: "For the homemade margarita mix:\n4 oz lime juice\n4 oz water\n1/4 cup sugar\n\nFor the margarita:\n1 lb cantaloupe wedges (1/2 a 3lb cantaloupe, seeds and skin removed)\n4 oz orange liqueur\n6 oz silver tequila\nSalt, for rimming (optional)\nLime wedges, for serving (optional)", direction: "Add the lime juice to a pitcher.Over medium heat in a small sauce pan, heat sugar and water, stirring until clear.\nRemove from heat and add to the lime juice.\n\nIn a food processor or blender, process the cantaloupe for ~60 seconds until completely obliterated into a fine pulp (you might need to add a few pieces at a time if using a blender).\n\nPour pulp into a fine mesh strainer set over a large measuring cup or small bowl and strain, lightly pressing the solids until you get 1 cup of liquid.\n\nAdd cantaloupe juice to the pitcher, along with tequila and orange liqueur.\n\nServe in salt-rimmed glasses, over crushed ice.", recipe_link: "http://tideandthyme.com/cantaloupe-margaritas/", favorite: nil},
  {cocktail_name: "Brown Derby", ingredient: "1.5 oz bourbon\n0.75 oz grapefruit juice\n0.75 oz honey syrup", direction: "1. Shake over ice and strain into a chilled cocktail glass.", recipe_link: "https://www.boston.com/food/food/2021/02/11/how-to-make-a-brown-derby-cocktail-recipe/", favorite: nil},
  {cocktail_name: "Zacapa Sherry Old Fashioned", ingredient: "1.5 oz Ron Zacapa 23 Rum\n1 oz Pedro Ximenez sherry\n6 drops Chocolate Bitters\n1 zest of clementine peel for garnish", direction: "Build ingredients in a mixing glass.\nAdd ice.\nStir for 30 seconds.\nStrain into an Old Fashioned glass over 1 ice cube.\nGarnish with clementine.\n\n--Optional--\nPair with dark chocolate", recipe_link: "https://www.youtube.com/watch?v=BTDFlbcli8Q", favorite: nil},
  {cocktail_name: "Zacapa Old Fashioned", ingredient: "1.5 oz Zacapa 23 rum\n2 dashes Aromatic Bitters\n1 Sugar cube\nSplash of water\nOrange twist", direction: "1. Combine bitters, sugar cube and splash of water into a rocks glass and stir to combine.\n\n2. Add Zacapa 23 and large ice cube to glass and stir.\n\n3. Garnish with an orange twist.", recipe_link: "https://www.zacaparum.com/en-us/recipes/classic/rum-old-fashioned?ds_e=GOOGLE&ds_c=40006123_General-Recipe_ZACAP_ZACAPB_SA3_GADW_USA_NU_CON_AO_SEAH_PSEAC_TEAD_NU_ALBOS_NAT_NU_CPC_NU_KEW_A21%2B_CXD_EN_BM_RECP&ds_k=%2Bold+%2Bfashioned&gclid=Cj0KCQiAkMGcBhCSARIsAIW6d0BAzqz2vU9KE65AF47-KsYTvEzEX6T3PrzqmZ3birCcr1sveqbmN1gaAm40EALw_wcB&gclsrc=aw.ds", favorite: nil},
  {cocktail_name: "Zacapa Manhattan", ingredient: "1.5 oz Zacapa 23 rum\n0.75 oz Sweet Vermouth\n2 dashes Aromatic Bitters\nDark Chocolate, for garnish", direction: "1. Combine Zacapa Rum, vermouth and bitters into a glass with ice.\n\n2. Stir well until chilled and strain into a coupe glass.\n\n3. Garnish with a wedge of dark chocolate.", recipe_link: "https://www.zacaparum.com/en-us/recipes/classic/rum-manhattan", favorite: nil},
  {cocktail_name: "Zacapa Negroni", ingredient: "1.5 oz Zacapa 23 rum\n0.75 oz Italian Vermouth\n0.75 oz Italian Herbal Aperitif\nOrange twist", direction: "1. Combine all ingredients except orange twist in a rocks glass with ice.\n\n2. Stir gently and garnish with an orange twist.", recipe_link: "https://www.zacaparum.com/en-us/recipes/classic/zegroni", favorite: nil},
  {cocktail_name: "Zacapa Signature Old Fashioned", ingredient: "1.5 oz Ron Zacapa 23 rum\n0.25 oz simple syrup\n1 dash Aromatic Bitters\n1 dash Chocolate Bitters\nGrapefruit Peel, for garnish\nDark Chocolate, for garnish", direction: "1. Combine Zacapa Rum, simple syrup and bitters into a mixing glass with ice.\n\n2. Stir well and strain into a double Old Fashioned glass.\n\n3. Express grapefruit peel over drink and grate dark chocolate over cocktail.", recipe_link: "https://www.zacaparum.com/en-us/recipes/signature/chocolate-old-fashioned", favorite: nil},
  {cocktail_name: "La Llorona Cocktail", ingredient: "1.5 oz of mezcal or tequila\n1.5 oz lime juice\n1 oz agave nectar or simple syrup\n0.50 ounce jalapeño purée\n\nJalapeño Purée:\n5 small fresh jalapeño chiles\n1/4 cup habanero pepper\n1 tablespoon serrano pepper\n1/2 cup water", direction: "Mix all ingredients together. Garnish with lime and a jalapeño slice.\n\nJalapeño Purée:\nCore and remove all seeds from peppers and large dice. Blend all ingredients well in a blender or food processor. If you can't find one of the varieties of peppers, substitute with another pepper you can find.", recipe_link: "https://www.hgtv.com/lifestyle/entertaining/easy-cocktails-you-can-make-at-home-pictures", favorite: nil},
  {cocktail_name: "Zacapa Rum Fizz", ingredient: "1.5 oz Zacapa 23 rum\n0.75 oz Pineapple Juice\n0.75 oz Lime Juice\n0.75 oz Simple Syrup\nCoconut Milk\nSparkling Water\nPineapple Wedge & Fronds", direction: "1. Add Zacapa, pineapple juice, lime juice, and simple syrup to a shaker with ice.\n\n2. Shake well and double strain into a highball glass with fresh ice.\n\n3. Froth coconut milk in separate bowl with a frother or whisk until airy.\n\n4. Top cocktail with a splash of sparkling water and coconut foam.\n\n5. Garnish with pineapple slices and fronds.", recipe_link: "https://www.zacaparum.com/en-us/recipes/signature/rum-fizz", favorite: nil},
  {cocktail_name: "Zacapa Daiquiri", ingredient: "1.5 oz Zacapa 23 rum\n1 oz Fresh Lime Juice\n0.5 oz Simple Syrup\nLime Wheel", direction: "1. Combine all ingredients into a shaker filled with ice.\n\n2. Shake well and strain into a coupe glass.\n\n3. Garnish with a lime wheel.", recipe_link: "https://www.zacaparum.com/en-us/recipes/classic/daiquiri", favorite: nil},
  {cocktail_name: "Zacapa Sour", ingredient: "1.5 oz Zacapa 23 rum\n0.75 oz Fresh Lemon Juice\n0.75 oz Simple Syrup", direction: "1. Combine all ingredients into a shaker filled with ice.\n\n2. Shake well and strain into a coupe glass.", recipe_link: "https://www.zacaparum.com/en-us/recipes/classic/rum-sour", favorite: nil},
  {cocktail_name: "Snowshoe Grog", ingredient: "1.5 oz bourbon whiskey, or brandy\n0.5 oz peppermint schnapps", direction: "1. In an old-fashioned glass filled with ice, pour the bourbon or brandy and peppermint schnapps and stir.", recipe_link: "https://www.thespruceeats.com/snowshoe-grog-recipe-761499", favorite: nil},
  {cocktail_name: "Pomegranate Gin Cocktail", ingredient: "2 oz pomegranate juice\n2 oz fresh orange juice\n2 teaspoons honey\n4 oz gin\n2 oz sparkling water\nfresh thyme and orange slices for garnish", direction: "1. To a cocktail shaker filled with ice add pomegranate juice, orange juice, honey and gin. Secure lid. Shake until the cocktail shaker is frosted on the outside.\n\n2. Pour through a cocktail shaker over ice, and finish with sparkling water.\n\n3. Garnish with fresh thyme and orange slices. Serve.", recipe_link: "https://www.joyfulhealthyeats.com/festive-holiday-pomegranate-gin-cocktail/", favorite: nil},
  {cocktail_name: "Tanqueray Almond Cocktail", ingredient: "1 oz gin\n0.5 oz amaretto liqueur\n1 dash bitters\n1 splash sour mix\nMaraschino cherries, for garnish", direction: "1. In a snifter with ice, pour the gin, amaretto, and bitters.\n\n2. Top with a splash of sour mix.\n\n3. Garnish with maraschino cherries.", recipe_link: "https://www.thespruceeats.com/tanqueray-almond-cocktail-recipe-760126", favorite: nil},
  {cocktail_name: "Revolver", ingredient: "2 oz bourbon\n0.5 oz coffee liqueur\n2 dashes orange bitters", direction: "1. Add all ingredients to a rocks over a large piece of ice and stir.\n\n2. Garnish with a flamed orange peel or regular orange peel.", recipe_link: "https://robbreport.com/food-drink/spirits/best-revolver-recipe-bourbon-coffee-liqueur-cocktail-1234792648/amp/", favorite: nil},
  {cocktail_name: "Jungle Bird", ingredient: "1.5 oz Jamaican rum\n0.75 oz Campari\n1 oz pineapple juice, preferably fresh\n0.5 oz lime juice\n0.5 oz simple syrup\nPineapple wedge and frond, for garnish", direction: "1. Add ingredients to a cocktail shaker with ice; shake vigorously for 7 seconds.\n\n2. Strain into a double rocks glass over ice and garnish.", recipe_link: "https://www.themanual.com/food-and-drink/jungle-bird-tropical-cocktail-summer-2020-official-drink/?amp", favorite: nil},
  {cocktail_name: "Tequila Old Fashioned", ingredient: "1 tsp agave nectar\n2 slices blood orange, plus peel for garnish\n3 oz reposado tequila, such as Milagro\n1-2 dashes bitters", direction: "1. Muddle agave and orange slices in a cocktail shaker; add tequila and ice.\n\n2. Stir until well-chilled and strain into an old fashioned glass filled with ice.\n\n3. Add bitters and garnish with orange peel.", recipe_link: "https://huckberry.com/journal/posts/whiskey-cocktails-with-tequila", favorite: nil},
  {cocktail_name: "Tequila Manhattan", ingredient: "1.5 ounces Roca Patrón Añejo tequila\n1 oz sweet vermouth\n1 or 2 dashes mole or chocolate bitters\n1 Luxardo maraschino cherry, to garnish\n1 Lemon zest, to garnish (optional)", direction: "1, Add the tequila, sweet vermouth, and bitters to a mixing glass and fill with ice.\n\n2. Stir about 30 revolutions with a barspoon and strain into a coupe or small glass.\n\n3. Garnish with a maraschino cherry and/or a piece of lemon zest.", recipe_link: "https://huckberry.com/journal/posts/whiskey-cocktails-with-tequila", favorite: nil},
  {cocktail_name: "Highland Cup", ingredient: "6 cilantro leaves\n0.5 oz agave syrup (1 part nectar and 1 part water cook, boiled until nectar dissolves)\n2 oz golden tequila, preferably 7 Leguas Reposado\nIce\nCilantro sprigs, for garnish", direction: "1. Muddle the cilantro leaves with the agave syrup in the bottom of a chilled julep cup.\n\n2. Add the tequila and some crushed ice and stir.\n\n3. Top with more crushed ice and garnish with the cilantro sprigs.", recipe_link: "https://huckberry.com/journal/posts/whiskey-cocktails-with-tequila", favorite: nil},
  {cocktail_name: "Gin Rickey", ingredient: "2 oz gin\n0.5 oz freshly squeezed lime juice\n4 oz club soda, or to taste\nLime wedge, for garnish", direction: "1. Fill a highball glass with ice and pour the gin and lime juice over the ice.\n\n2. Top with club soda\n\n3. Garnish with a lime wedge", recipe_link: "https://www.thespruceeats.com/gin-rickey-recipe-760083", favorite: nil},
  {cocktail_name: "New York Sour", ingredient: "2 oz rye whiskey\n0.75 oz lemon juice\n0.75 oz simple syrup (1:1 sugar to water)\n0.5 to 1 oz light red wine\n\nNote on Wine: Style of wine matters less here than you might think. If I had one guiding principle, it would be not sweet. As long as its dry, the bookends of Pinot Noir and Cabernet work differently, but equally well. Again, if I had all options, I like the subtle, light fruit of a Pinot Noir or Beaujolais, but use what you have.", direction: "1. Add rye, lemon juice and simple syrup to the shaker tin with ice and shake hard for 10 to 12 seconds.\n\n2. Strain over fresh ice in a rocks glass, leaving 0.5-inch clearance on the top of the glass.\n\n3. Top with between 0.5 oz. and 1 oz. of light red wine. ", recipe_link: "https://robbreport.com/food-drink/spirits/new-york-sour-recipe-rye-whiskey-cocktail-2945266/amp/", favorite: nil},
  {cocktail_name: "Paloma (Fresh)", ingredient: "2 oz blanco tequila\n0.5 oz lime juice\n1 oz grapefruit juice\n0.5 oz simple syrup\n3 to 4 oz soda water", direction: "1. Shake on ice. \n\n2. Strain over fresh ice in a tall glass with a salted rim and top with soda water.\n\n3. Garnish with a grapefruit peel.", recipe_link: "https://robbreport.com/food-drink/spirits/paloma-recipe-tequila-grapefruit-soda-lime-cocktail-1234568341/amp/", favorite: nil},
  {cocktail_name: "Old Pal", ingredient: "1.5 oz bourbon or rye whiskey\n1 oz dry vermouth\n1 oz Campari\nLemon peel for garnish", direction: "1. Combine the whiskey, vermouth, and Campari in a cocktail mixing glass (or any other type of glass). Fill the mixing glass with 1 handful ice and stir continuously for 30 seconds.\n\n2. Strain into cocktail glass. Garnish with a lemon peel.", recipe_link: "https://www.acouplecooks.com/old-pal-cocktail/", favorite: nil},
  {cocktail_name: "Negroni", ingredient: "1 oz gin\n1 oz sweet or semi-sweet red vermouth\n1 oz Campari\nIce for serving\nOrange peel for garnish", direction: "1. Combine the gin, sweet vermouth, and Campari in a cocktail mixing glass (or any other type of glass). Fill the mixing glass with 1 handful ice and stir continuously for 30 seconds.\n\n2. Add ice to a lowball glass, and strain the drink into the glass (or you can use a cocktail glass without ice).\n\n3. Use a knife to remove a 1″ wide strip of the orange peel. Squeeze the orange peel into the drink to release the oils. Gently run the peel around the edge of the glass, then place it in the glass and serve.", recipe_link: "https://www.acouplecooks.com/classic-negroni/", favorite: nil},
  {cocktail_name: "Paper Plane", ingredient: "0.75 oz bourbon\n0.75 oz Nonino Quintessentia amaro\n0.75 oz ounce Aperol\n0.75 oz ounce fresh lemon juice", direction: "1. Combine all the ingredients in a cocktail shaker three-quarters filled with ice.\n2. Shake until chilled.\n3. Strain into a coupe glass.", recipe_link: "https://punchdrink.com/recipes/paper-plane/", favorite: nil},
  {cocktail_name: "Pegu Club", ingredient: "2 oz gin\n0.75 oz dry Curaçao\n0.75 oz lime juice\n1 dash Angostura bitters\n1 dash orange bitters\nGarnish: lime peel", direction: "1. Add all ingredients to a cocktail shaker.\n\n2. Add ice and shake until chilled.\n\n3. Strain into a chilled coupe or cocktail glass.\n\n4. Garnish with a lime peel.", recipe_link: "https://punchdrink.com/recipes/pegu-club/", favorite: nil},
  {cocktail_name: "Alaska Cocktail", ingredient: "2.25 oz Gin\n0.75 oz Yellow Chartreuse\n1 dash orange bitters", direction: "1. Stir on ice for 20 to 30 seconds. \n2. Strain off ice into chilled coupe glass, and garnish with a lemon peel.", recipe_link: "https://robbreport.com/food-drink/spirits/alaska-cocktail-recipe-gin-yellow-chartreuse-bitters-1234574893/amp/", favorite: nil},
  {cocktail_name: "Oaxaca Old Fashioned", ingredient: "1.5 oz El Tesoro reposado tequila\n0.5 oz Del Maguey San Luis Del Rio mezcal\n2 dashes Angostura bitters\n1 barspoon agave nectar\nGarnish: flamed orange twist", direction: "1, Combine all the ingredients except the orange twist in an Old-Fashioned glass filled with one large ice cube.\n\n2. Stir until chilled.\n\n3. Top with a flamed orange twist, then drop the twist into the drink.", recipe_link: "https://punchdrink.com/recipes/oaxaca-old-fashioned/", favorite: nil},
  {cocktail_name: "Fall Dark & Stormy", ingredient: "2 oz apple cider\n2 oz bourbon\n0.5 oz freshly squeezed lemon juice\n2 oz Ginger Beer\n1 cup ice\nApple slices and cinnamon stick for garnish", direction: "1. In a cocktail shaker filled with ice, combine apple cider, bourbon and lemon juice. Shake well. \n\n2. Strain into a glass filled with ice.\n\n3. Top with ginger beer and garnish with apple slices and cinnamon stick.", recipe_link: "https://thebeachhousekitchen.com/the-fall-dark-n-stormy/#wprm-recipe-container-19138", favorite: nil},
  {cocktail_name: "Moose Milk", ingredient: "Makes 2 drinks, maybe\n\n1 cup cold coffee\n1 cup half & half\n1.5 cups vanilla ice cream\n2 oz rum, whiskey, and/or vodka\n1 oz Kahlua\nNutmeg or dark chocolate shavings, to garnish", direction: "1. Dump all the ingredients (except the garnish) into a bowl and whisk maniacally.\n\n2. Let it sit for a minute, so the ice cream melts.\n\n3. Top the mixture with the nutmeg or dark chocolate shavings.\n\n4. Add a floater of rum if it feels right.", recipe_link: "https://www.atlasobscura.com/articles/what-is-moose-milk-canadian-holiday-cocktail.amp", favorite: nil},
  {cocktail_name: "Red Hook", ingredient: "2 oz rye whiskey\n0.5 oz Punt e Mes Sweet Vermouth\n0.25 oz Luxardo Maraschino Liqueur", direction: "1. Stir on ice for 15 seconds (if small ice) to 30 seconds (if large ice).\n\n2. Strain off ice into chilled coupe glass.\n\n3. Garnish with a cocktail cherry or nothing at all. ", recipe_link: "https://robbreport.com/food-drink/spirits/red-hook-cocktail-recipe-rye-whiskey-maraschino-1234596861/amp/", favorite: nil},
  {cocktail_name: "El Lúpolo", ingredient: "1.5 oz blanco tequila\n0.5 oz lime juice\n0.5 oz Campari\n0.5 oz simple syrup\nTop with about 3 oz. IPA", direction: "1. Add all ingredients except for beer into a shaker tin and shake on ice for 6 to 8 seconds.\n\n2. Strain into a tall glass and top with about 3 oz. IPA.\n\n3. Garnish with a grapefruit peel.", recipe_link: "https://robbreport.com/food-drink/spirits/best-el-lupolo-recipe-beer-tequila-campari-cocktail-ipa-1234598858/amp/", favorite: nil},
  {cocktail_name: "Autumn Delight", ingredient: "2 oz Japanese whisky\n4 oz apple cider\n1 tsp ground cinnamon\n2 apple slices, for garnish\n1 cinnamon stick, for garnish", direction: "1. Pour the ingredients into a cocktail shaker filled with ice.\n\n2. Shake and strain into an ice-filled old-fashioned glass.\n\n3. Dust with fresh cinnamon and garnish with apple slices and a cinnamon stick. Serve and enjoy.", recipe_link: "https://www.thespruceeats.com/yamazaki-autumn-delight-recipe-761408", favorite: nil},
  {cocktail_name: "The Red Akuma", ingredient: "1 cup of blood orange juice\n2 teaspoons of Agave\n4 oz Japanese whisky\n1 average sized jalapeno, chopped with stem removed (about 2 teaspoons)", direction: "1. Mix blood orange juice, agave and whisky in a cocktail shaker.\n\n2. Stir until perfectly combined.\n\n3. Add jalapeño, replace the cup, and shake three or four times.\n\n4. Pour through a strainer into glasses filled with ice. Discard the jalapeño.", recipe_link: "https://dekanta.com/japanese-whisky-cocktail-recipes/amp/", favorite: nil},
  {cocktail_name: "Bourbon Old Fashioned", ingredient: "1 teaspoon sugar\n3 dashes Angostura bitters\n1 teaspoon water\n2 oz bourbon\nGarnish: orange twist", direction: "1. Add the sugar and bitters into a mixing glass, then add the water, and stir until the sugar is nearly dissolved.\n\n2. Fill the mixing glass with ice, add the bourbon, and stir until well-chilled.\n\n3. Strain into a rocks glass over one large ice cube.\n\n4. Express the oil of an orange twist over the glass, then drop into the glass to garnish.", recipe_link: "https://www.liquor.com/recipes/bourbon-old-fashioned/", favorite: nil},
  {cocktail_name: "Mezcal Gimlet", ingredient: "2 oz mezcal blanco (80 proof)\n\n1 oz fresh lime juice (approx. 1 lime)\n\n0.5 oz mint syrup", direction: "1. Pour ingredients into a shaker with ice and shake vigorously.\n\n2. Strain into a chilled coupe glass.\n\n*To make the mint syrup, simmer 40 fresh mint leaves in 32 oz simple syrup (1:1 sugar and water) for 30 minutes.", recipe_link: "https://www.esquire.com/food-drink/drinks/recipes/a13705/mezcal-gimlet-recipe-041612/", favorite: nil},
  {cocktail_name: "Sake Negroni Bramble", ingredient: "1 oz Campari\n1 oz sake\n1 oz white vermouth", direction: "1. Crush ice and fill collins glass.\n\n2. Pour the sake and white vermouth directly into the glass.\n\n3. Top with Campari for effect.\n\n4. Stir quickly to mix ingredients together.", recipe_link: "https://beautifulbooze.com/sake-negroni-bramble/", favorite: nil},
  {cocktail_name: "Summer Negroni", ingredient: "1 oz Gin\n1 oz Aperol\n1 oz dry vermouth\n1 dash of orange bitters\n1 twist of orange peel", direction: "Stir, strain up, garnish", recipe_link: "https://www.cocktailbuilder.com/recipe/summer-negroni", favorite: nil},
  {cocktail_name: "Texas Ranch Water", ingredient: "3 oz blanco tequila\n1.5 oz fresh lime juice\nTopo Chico, chilled\nFresh lime wedge for garnish", direction: "1. Add tequila and fresh lime juice to a highball glass filled with ice. \n\n2. Top with Topo Chico.\n\n3. Add fresh lime wedge to the rim of the glass.", recipe_link: "https://www.southernliving.com/recipes/ranch-water", favorite: nil},
  {cocktail_name: "Gin Basil Smash", ingredient: "2 oz gin\n1 oz lemon juice\n0.75 ounce simple syrup\nBasil leaves\nGarnish: basil leaves", direction: "1. Muddle basil leaves in a shaker tin.\n\n2. Add remaining ingredients and ice and shake until chilled.\n\n3. Double strain into a rocks glass filled with ice.\n\n4. Garnish with basil leaves.", recipe_link: "https://punchdrink.com/recipes/gin-basil-smash/", favorite: nil},
  {cocktail_name: "Kingston Negroni", ingredient: "1 oz Jamaican rum\n1 oz Campari\n1 oz sweet vermouth\nGarnish: long, trimmed orange peel", direction: "1. Add all ingredients to a mixing glass.\n\n2. Add ice and stir until chilled.\n\n3. Strain into over ice (preferably a large cube) into a rocks glass.\n4. Garnish with a long, trimmed orange peel.", recipe_link: "https://punchdrink.com/recipes/kingston-negroni/", favorite: nil},
  {cocktail_name: "Boulevardier", ingredient: "2 oz bourbon\n0.75 oz Campari\n0.75 oz sweet vermouth\nOrange peel, for garnish", direction: "1. Stir bourbon, Campari, and vermouth in an ice-filled shaker.\n\n2. Strain into a cocktail glass filled with fresh ice.\n\n3. Flame the orange peel atop the drink: Squeeze the peel side toward the cocktail to express the oils and light a match in between the peel and the drink; drop peel into cocktail.", recipe_link: "https://www.saveur.com/article/recipes/boulevardier-bourbon-negroni/", favorite: nil},
  {cocktail_name: "Rob Roy", ingredient: "2 oz blended scotch whisky\n1 oz Italian vermouth\n2 dashes Angostura bitters\nCocktail cherry", direction: "1. Stir the scotch, vermouth, and bitters well with cracked ice. \n\n2. Strain into a chilled cocktail glass. \n\n3. Garnish with a cocktail cherry.", recipe_link: "https://www.esquire.com/food-drink/drinks/recipes/a3716/rob-roy-drink-recipe/", favorite: nil},
  {cocktail_name: "Toki Highball", ingredient: "1 part Suntory Whisky TOKI\n3 parts chilled premium soda water\nGrapefruit or lemon peel, for garnish (optional)", direction: "1. Fill a highball glass to the brim with ice.\n\n2. Add Suntory Whisky TOKI, stir to cool the whisky and glass, and pour in chilled soda water.\n\n3. Add a twist of grapefruit citrus (optional). Enjoy.", recipe_link: "https://www.veranda.com/luxury-lifestyle/entertaining/g37146810/whiskey-cocktails/", favorite: nil},
  {cocktail_name: "The Painkiller", ingredient: "2 oz Pusser’s Rum or Jamaican rum\n4 oz pineapple juice\n1 oz orange juice\n1 oz coconut cream", direction: "1. Shake on crushed or cracked ice for 4 to 6 seconds and dump contents into a tall glass or tiki mug.\n\n2. Top with more crushed ice.\n\n3. Garnish with a grind of nutmeg if you have it, or a slice or orange, or pineapple leaves, or all three.", recipe_link: "https://robbreport.com/food-drink/spirits/best-painkiller-recipe-rum-coconut-oj-pineapple-1234626579/amp/", favorite: nil},
  {cocktail_name: "Whiskey Smash", ingredient: "2 oz bourbon (something wheated, like Makers Mark or Weller Reserve)\n1 oz simple syrup (or 0.75 oz)\n4 mint leaves\n0.5 a lemon (cut into 4 pieces)", direction: "1. Muddle mint and lemon and simple syrup together.\n\n2. Add whiskey, dry shake (no ice).\n\n3. Strain in a lowball over crushed ice.\n\n4. Garnish with mint sprig crown.", recipe_link: "https://www.boston.com/food/food/2021/02/11/cocktail-recipe-how-to-make-a-classic-whiskey-smash/?amp=1", favorite: nil},
  {cocktail_name: "Vancouver Cocktail", ingredient: "2 oz gin\n0.5 oz sweet vermouth\n0.25 oz Bénédictine\n2 dashes of orange bitters\nlemon twist, for garnish", direction: "1. In an ice-filled mixing glass, combine ingredients.\n\n2. Stir until well-chilled (about 30 seconds).\n\n3. Strain into a cocktail glass.\n\n4. Garnish with the lemon twist.", recipe_link: "https://www.themanual.com/food-and-drink/the-vancouver-cocktail-recipe/?amp", favorite: nil},
  {cocktail_name: "Old Cuban", ingredient: "1.5 oz aged rum (ie Bacardi 8)\n0.75 oz lime juice\n0.75 oz simple syrup (or 1 oz)\n6-8 mint leaves\n2 dashes Angostura Bitters\n2 oz Champagne (or Cremant)", direction: "1. Add all ingredients except champagne to a cocktail shaker with ice.\n\n2. Shake well for 10 to 12 seconds and strain into a flute or stemmed cocktail glass.\n\n3. Top with wine and garnish with a mint leaf or sprig.", recipe_link: "https://robbreport.com/food-drink/spirits/best-old-cuban-recipe-how-to-make-rum-champagne-cocktail-1234620913/amp/", favorite: nil},
  {cocktail_name: "Hemingway Daiquiri", ingredient: "2 oz white rum\n0.75 oz lime juice\n0.5 oz grapefruit juice\n0.25-0.5 o. simple syrup (to taste)\n0.25 oz Luxardo Maraschino liqueur\nGrapefruit peel, for garnish", direction: "1. Combine ingredients in a cocktail shaker with ice, shake well for 10 seconds, and strain into a stemmed glass.\n\n2. Garnish with a grapefruit peel.", recipe_link: "https://robbreport.com/food-drink/spirits/best-hemingway-daiquiri-recipe-rum-marschino-cocktail-1234615329/amp/", favorite: nil},
  {cocktail_name: "Padua Paloma", ingredient: "1.5 oz blanco tequila\n0.75 oz Aperol        \n0.5 oz fresh lime juice \n3 oz fresh grapefruit juice\n1 oz sparkling water", direction: "1. Combine first four ingredients in a Collins glass over ice.\n\n2. Top with sparkling water, and stir with a straw.\n\n3. Garnish with dehydrated grapefruit wheel and black salt.", recipe_link: "https://punchdrink.com/recipes/padua-paloma/", favorite: nil},
  {cocktail_name: "Josh Goldman’s Paloma", ingredient: "2 oz blanco tequila, preferably Siembra Azul\n0.5 oz lime juice\n1 pinch sea salt\nSquirt, to top", direction: "1. Add the tequila, lime juice and a pinch of salt into a Collins glass over ice.\n\n2. Top with Squirt.\n\n3. Garnish with a thick wedge of grapefruit.", recipe_link: "https://punchdrink.com/recipes/josh-goldmans-paloma/", favorite: nil},
  {cocktail_name: "Ward 8", ingredient: "2 - 2.5 oz bourbon or rye whiskey\n0.75 oz lemon juice\n0.75 oz ounce orange juice\n1 dash / 1 tsp grenadine\nMaraschino cherry, for garnish\nOrange slice, for garnish", direction: "1. In a cocktail shaker, add the whiskey, lemon and orange juices, and grenadine. Fill with ice and shake.\n\n2. Strain into a chilled cocktail glass or a Collins glass filled with ice.\n\n3. Garnish with a cherry and orange slice.", recipe_link: "https://www.thespruceeats.com/ward-eight-cocktail-recipe-760755", favorite: nil},
  {cocktail_name: "Negroni Sbagliato", ingredient: "1 oz sweet red vermouth\n1 oz Campari\n1 oz Prosecco, champagne or other sparkling wine\nIce for serving\nOrange peel for garnish", direction: "1. Stir the vermouth and Campari in a lowball glass. Add ice.\n\n2. Top off the glass with the sparkling wine. Garnish with an orange peel and serve.", recipe_link: "https://www.acouplecooks.com/negroni-sbagliato/", favorite: nil},
  {cocktail_name: "Tequila Sunrise", ingredient: "2 oz tequila\n4 oz orange juice\n0.75 oz grenadine syrup (purchased or homemade)\nIce\nFor the garnish: Cocktail cherry, orange slice", direction: "1. Fill a highball glass with ice. Pour in the tequila and orange juice and stir.\n\n2. Pour the grenadine into center of the drink and it will sink to the bottom. Stir gently for sunrise gradient effect.\n\n3. Garnish with an orange slice and cocktail cherry.", recipe_link: "https://www.acouplecooks.com/tequila-sunrise/", favorite: nil},
  {cocktail_name: "Classic Clover Club", ingredient: "3 oz gin\n0.75 oz raspberry syrup or grenadine syrup (purchased or homemade)\n1 oz fresh lemon juice\n1 egg white\nFor the garnish: lemon twist, fresh raspberries (optional)", direction: "1. Add the gin, raspberry syrup or grenadine, lemon juice, and egg white to a cocktail shaker without ice. Shake for 15 seconds.\n\n2. Add the ice to the cocktail shaker. Shake again for 30 seconds.\n\n3. Strain the drink into a cocktail glass; the foam will collect at the top. Garnish with a lemon twist or fresh raspberries.", recipe_link: "https://www.acouplecooks.com/clover-club-cocktail/", favorite: nil},
  {cocktail_name: "Mezcal Negroni", ingredient: "1 oz mezcal\n1 oz Campari\n1 oz sweet vermouth\nGarnish: orange half-wheel", direction: "1. Add the mezcal, Campari, and sweet vermouth into a mixing glass with ice and stir until well-chilled.\n\n2. Strain into a rocks glass over one large ice cube.\n\n3. Garnish with an orange half-wheel.", recipe_link: "https://www.liquor.com/recipes/mezcal-negroni/", favorite: nil},
  {cocktail_name: "The Islay Whisky Sour", ingredient: "2 oz Islay Whisky\n0.5 oz ounce of simply syrup\n0.5 oz of freshly squeezed lemon juice\n1 egg white\n2 dash of Bitters\nGarnish: a cherry and orange slice.", direction: "1. Add egg white, lime juice, simple syrup and 1 dash of bitters to a shaker without ice and shake for 10 seconds.\n\n2. Add whisky to cocktail glass filled with crushed ice, and then add ingredients from the shaker.\n\n3. Add 1 dash of bitters to the top.\n\n4. Garnish with an orange peal.", recipe_link: "https://islaycottages.com/blog/5-inspired-islay-whisky-cocktails/", favorite: nil},
  {cocktail_name: "Mary Pickford", ingredient: "2 oz white rum\n1.5 - 2 oz pineapple juice\n1 teaspoon Maraschino liqueur (optional)\n1 - 2 teaspoons grenadine\nFor the garnish: Maraschino cherry", direction: "1. Place the rum, pineapple juice, Maraschino liqueur, and grenadine in a cocktail shaker. Add 2 handfuls of ice and shake until cold.\n\n2. Strain the drink into a cocktail glass. Garnish with a Luxardo cherry if desired.", recipe_link: "https://www.acouplecooks.com/mary-pickford-cocktail/", favorite: nil},
  {cocktail_name: "Delilah", ingredient: "1.5 gin\n0.75 oz premium triple sec\n0.75 oz lemon juice", direction: "1. In a cocktail shaker filled with ice, pour the gin, triple sec, and lemon juice.\n\n2. Shake well and strain into a chilled cocktail glass.", recipe_link: "https://www.thespruceeats.com/delilah-cocktail-recipe-759293", favorite: nil},
  {cocktail_name: "Belmont Jewel", ingredient: "1.5 ounces bourbon whiskey\n2 oz lemonade\n1 oz pomegranate juice\nLemon wedge, orange zest, or ​cherry, for garnish", direction: "1. In a cocktail shaker filled with ice, combine the ingredients.\n\n2. Shake vigorously.\n\n3. Strain over fresh ice into a double old-fashioned glass and garnish.", recipe_link: "https://www.thespruceeats.com/woodford-reserve-belmont-jewel-recipe-761402", favorite: nil},
  {cocktail_name: "Prosecco Mint Julep", ingredient: "6 mint leaves, plus more for serving\n0.75 oz simple syrup\n1 oz bourbon\nCrushed ice\n3 oz. dry Prosecco\n1 dash of Angostura bitters", direction: "1. In a mint julep cup or highball glass, muddle mint leaves and simple syrup with a muddler or the end of a wooden spoon handle until mint is dark green and has lost its shape.\n\n2. Add bourbon, then ice, Prosecco, and bitters, and gently stir to combine. Garnish with more mint.", recipe_link: "https://12ft.io/proxy?q=https%3A%2F%2Fwww.delish.com%2Fholiday-recipes%2Fchristmas%2Fa42004813%2Fprosecco-mint-julep-recipe%2F", favorite: nil},
  {cocktail_name: "Blueberry Lavender Vodka Spritzer", ingredient: "Makes 8 drinks\n\nFor the Simple Syrup:\n\n1 cup water\n1 cup sugar\n1 cup blueberries, fresh or frozen\n4 sprigs organic fresh lavender, or 1 tablespoon dry culinary lavender\n\n\nFor the Vodka Spritzer:\n\n2 cups vodka\n4 oz freshly squeezed lime juice\n1 liter club soda\n1 cup blueberries, fresh or frozen\nFresh lavender sprigs, for garnish", direction: "Make the Simple Syrup\n1. Combine the water, sugar, and blueberries in a saucepan and bring to a boil over medium-high heat. Stir well to dissolve the sugar.\n\n2. Reduce the heat to a low simmer and add the lavender sprigs or dried culinary lavender. Let simmer for about 10 minutes.\n\n3. Strain the syrup into a jar; as you pour, use a masher or back of a spoon to squeeze all the remaining juice from the berries.\n\nMake the Cocktail\n1. In a glass pitcher, combine the vodka, 3/4 cup of the blueberry lavender simple syrup, the lime juice, and the liter of club soda. \n\n2. Add ice and a healthy handful of blueberries.\n\n3. When you’re ready to pour, you can garnish each glass with some more blueberries and a sprig of lavender. Serve.", recipe_link: "https://www.thespruceeats.com/blueberry-lavender-vodka-spritzer-4133980", favorite: nil},
  {cocktail_name: "The Salty Dog", ingredient: "2 oz gin\n4 oz grapefruit juice\nLemon wedge, or lime wedge, for garnish\nCoarse salt, for rimming", direction: "1. Rim a Collins glass with salt: Rub a lemon or lime wedge around the rim, then roll it in a shallow dish of coarse salt.\n\n2. Fill the glass with ice and add the gin and juice. Stir well.\n\n3. Garnish with a lemon or lime wedge.", recipe_link: "https://www.thespruceeats.com/salty-dog-cocktail-recipe-759802", favorite: nil},
  {cocktail_name: "Sidecar", ingredient: "1.5 oz cognac\n0.75 oz orange liqueur (such as Cointreau)\n0.75 oz lemon juice, freshly squeezed\nGarnish: orange twist\nGarnish: sugar rim (optional)", direction: "1. Coat the rim of a coupe glass with sugar, if desired, and set aside.\n\n2. Add the cognac, orange liqueur and lemon juice to a shaker with ice and shake until well-chilled.\n\n3. Strain into the prepared glass.\n\n4. Garnish with an orange twist.", recipe_link: "https://www.liquor.com/recipes/sidecar/", favorite: nil},
  {cocktail_name: "French 75", ingredient: "1 oz gin\n0.5 oz lemon juice, freshly squeezed\n0.5 oz simple syrup\n3 oz Champagne (or other sparkling wine)\nGarnish: lemon twist", direction: "1. Add the gin, lemon juice and simple syrup to a shaker with ice and shake until well-chilled.\n\n2. Strain into a Champagne flute.\n\n3. Top with the Champagne.\n\n4. Garnish with a lemon twist.", recipe_link: "https://www.liquor.com/recipes/french-75-2/", favorite: nil},
  {cocktail_name: "Corpse Reviver No. 2", ingredient: "1 oz gin\n1 oz Lillet Blanc or Cocchi Americano\n1 oz Cointreau\n1 oz fresh lemon juice\n1 dash absinthe\n1 orange peel", direction: "1. To an ice-filled cocktail shaker, add the Lillet Blanc, Cointreau, lemon juice, gin, and absinthe.\n\n2. Shake vigorously until very cold, about 15 seconds.\n\n3. Strain into a chilled martini glass, garnish with a twist of orange peel, and serve immediately.", recipe_link: "https://www.saveur.com/article/Recipes/Corpse-Reviver-No-2/", favorite: nil},
  {cocktail_name: "Irish Ale", ingredient: "2 oz Irish whiskey\n3 lime wedges\n3 ounces ginger beer", direction: "1. Pour the whiskey into a highball glass.\n\n2. Squeeze the juice from all 3 lime wedges on top of the whiskey.\n\n3. Fill the glass with ice.\n\n4. Top with ginger beer and stir well.", recipe_link: "https://www.thespruceeats.com/irish-ale-cocktail-enjoy-your-whiskey-4119074", favorite: nil},
  {cocktail_name: "Grapefruit Cardamom Gin Fizz", ingredient: "2 oz rosemary syrup (recipe below)\n3 oz cardamom infused gin (recipe below)\n0.5 cup grapefruit juice\nsplash of prosecco, chilled\nrosemary sprigs\ngrapefruit peel, for garnish\n\n\nCardamom Infused Gin:\n1 cup gin\n6 cardamom pods\n\nRosemary Syrup:\n1 cup sugar\n1 cup water\n2 rosemary sprigs", direction: "1. In a small jar with an airtight lid add gin and cardamom pods. Seal and store for 24 hours.\n\n2. To make the rosemary simple syrup, bring sugar, water and 2 rosemary sprigs to a simmer in a small saucepan until sugar is dissolved. Remove from heat and allow to cool completely. Remove the rosemary and use right away, or store in an airtight container in the refrigerator for up to a week.\n\n3. To make the cocktail pour rosemary syrup, infused gin and grapefruit juice over ice in a cocktail mixer and shake to combine.\n\n4. Using a strainer, divide the mixture in two glasses filled with fresh ice.\n\n5. Top with prosecco and garnish with a sprig of rosemary and a grapefruit peel.", recipe_link: "https://themodernproper.com/grapefruit-cardamom-gin-fizz", favorite: nil},
  {cocktail_name: "Moscow Mule", ingredient: "2 oz Vodka\n4 oz Ginger beer\n0.75 oz Lime juice\nGarnish: Lime wedge", direction: "1. Add the vodka and lime juice to a copper mug.\n\n2. Fill the mug with ice and top with ginger beer.\n\n3. Garnish with a lime wedge", recipe_link: "https://www.insider.com/guides/kitchen/vodka-cocktails?amp", favorite: nil},
  {cocktail_name: "White Russian", ingredient: "2 oz Vodka\n1 oz Coffee liqueur (Kahlua)\n1 oz Heavy cream", direction: "1. Mix the vodka and coffee liqueur in and old fashioned glass.\n\n2. Fill the glass with ice.\n\n3. Top with heavy cream.", recipe_link: "https://www.insider.com/guides/kitchen/vodka-cocktails?amp", favorite: nil},
  {cocktail_name: "Salty Dog", ingredient: "2 oz Vodka\n4 oz Grapefruit juice\nSalt (for rim of glass)\nGarnish: Lime wheel", direction: "1. Salt the rim of a highball or old fashioned glass.\n\n2. Mix in the vodka and grapefruit juice.\n\n3. Fill the glass with ice and garnish with a lime wheel.", recipe_link: "https://www.insider.com/guides/kitchen/vodka-cocktails?amp", favorite: nil},
  {cocktail_name: "Chi Chi", ingredient: "2 oz Vodka\n4 oz Pineapple juice\n1 oz Cream of coconut\nGarnish: Pineapple wedge", direction: "1. Add all the ingredients to a shaker filled with ice and shake vigorously.\n\n2. Pour into a glass with crushed ice and garnish with a pineapple wedge.", recipe_link: "https://www.insider.com/guides/kitchen/vodka-cocktails?amp", favorite: nil},
  {cocktail_name: "Lemon Drop", ingredient: "2 oz Vodka\n1 oz Lemon juice\n0.5 oz Curaçao \n0.5 oz Simple syrup\nSugar (for rim of glass)\nGarnish: Lemon peel", direction: "1. Coat the rim of the coupe glass with lemon juice, then dip in sugar to coat.\n\n2. Mix the vodka, lemon juice, Curaçao and simple syrup into a cocktail shaker with with ice and shake until chilled.\n\n3. Strain directly into the glass.", recipe_link: "https://www.insider.com/guides/kitchen/vodka-cocktails?amp", favorite: nil},
  {cocktail_name: "Ivy Gimlet", ingredient: "2 oz Vodka\n1 oz Lime juice\n0.75 oz Simple syrup\nSmall handful of mint", direction: "1. Lightly muddle mint in a shaker filled with ice and add all the ingredients.\n\n2. Shake for 10 seconds.\n\n3. Double strain into a couple glass.", recipe_link: "https://www.insider.com/guides/kitchen/vodka-cocktails?amp", favorite: nil},
  {cocktail_name: "The Southside", ingredient: "1 oz lemon juice, freshly squeezed\n2 oz gin\n1 oz simple syrup\n5 mint leaves\nGarnish: mint sprig", direction: "1. Add the mint leaves and lemon juice into a shaker and gently muddle.\n\n2. Add the gin and simple syrup with ice, and shake until well-chilled.\n\n3. Double-strain into a chilled cocktail glass.\n\n4. Garnish with a mint sprig.", recipe_link: "https://www.liquor.com/recipes/south-side/", favorite: nil},
  {cocktail_name: "Easy Eggnog", ingredient: "1 oz Amaretto\n1 oz vodka\n2 oz store bought eggnog\npinch of nutmeg\npinch of cinnamon\nCaramel sauce for rimming the glass", direction: "1. Rim a cocktail glass with caramel sauce.\n\n2. Shake the eggnog, Amaretto, vodka, nutmeg and cinnamon together in a shaker filled halfway with ice.\n\n3. Strain into a cocktail glass and garnish with more cinnamon if desired.", recipe_link: "https://www.mantitlement.com/eggnog-cocktail/", favorite: nil},
  {cocktail_name: "Mrs. Claus", ingredient: "1 cup eggnog\n1 oz white chocolate liqueur\n1 tablespoon peppermint Schnapps\nWhipped cream for garnish\nCrushed candy canes for garnish", direction: "1. Pour the eggnog, white chocolate liqueur, and peppermint Schnapps into desired glass and stir to combine completely.\n\n2. Garnish with a dollop of whipped cream and a sprinkle of crushed candy cane.", recipe_link: "https://kjandcompany.co/mrs-claus-cocktail/", favorite: nil},
  {cocktail_name: "Midnight Marauder", ingredient: "1 oz mezcal, preferably Del Maguey Vida\n1 oz Bonal\n1 oz Cynar\n1 dash Bittermen's Mole Bitters", direction: "1. Combine all ingredients in a mixing glass over ice and stir until chilled.\n\n2. Strain into a chilled coupe or cocktail glass.", recipe_link: "https://punchdrink.com/recipes/midnight-marauder/", favorite: nil},
  {cocktail_name: "Toronto", ingredient: "2 oz rye\n0.25 oz Fernet-Branca\n0.25 oz demerara syrup\n2 dashes Angostura bitters", direction: "1. Combine all ingredients in a mixing glass with ice and stir until chilled.\n\n2. Strain into a chilled coupe or cocktail glass.\n\n3. Garnish with an orange twist.", recipe_link: "https://punchdrink.com/recipes/toronto/", favorite: nil},
  {cocktail_name: "Hanky Panky", ingredient: "1.5 oz gin\n1.5 oz sweet vermouth\n1 bar spoon Fernet Branca", direction: "1. Add all ingredients to a mixing glass.\n\n2. Add ice and stir until chilled.\n\n3. Strain into a chilled coupe or cocktail glass.\n\n4. Garnish with an orange peel.", recipe_link: "https://punchdrink.com/recipes/hanky-panky/", favorite: nil},
  {cocktail_name: "Sharon", ingredient: "1.5 oz manzanilla sherry\n1 oz sweet vermouth, preferably Carpano Antica Formula\n0.75 oz Campari", direction: "1. Combine all ingredients in a mixing glass over ice and stir until chilled.\n2. Strain into a Nick & Nora glass.\n3. Express the orange twist over the glass and discard.", recipe_link: "https://punchdrink.com/recipes/sharon/", favorite: nil},
  {cocktail_name: "Manhattan", ingredient: "2 oz rye whiskey\n1 oz sweet vermouth\n2 dashes Angostura bitters\nGarnish: brandied cherry (or lemon twist, if preferred)", direction: "1. Add the rye whiskey, sweet vermouth, and bitters into a mixing glass with ice and stir until well-chilled.\n\n2. Strain into a chilled Nick & Nora or coupe glass.\n\n3. Garnish with a brandied cherry (or a lemon twist, if preferred).", recipe_link: "https://www.liquor.com/recipes/manhattan-2/", favorite: nil},
  {cocktail_name: "The Mizuwari (whisky highball)", ingredient: "1.5 oz whisky (Suntory Toki is preferred)\n3 oz soda water", direction: "1. Add three large ice cubes to a highball glass and stir, pouring out any melted water.\n\n2. Add the whisky and stir again 10 times.\n\n3. Add the soda water and stir several more times.", recipe_link: "https://www.cntraveler.com/gallery/how-to-bring-japanese-cocktail-culture-to-your-home-bar?utm_source=nl&utm_brand=cnt&utm_mailing=CNT_Daily_PM_021022&utm_campaign=aud-dev&utm_medium=email&bxid=5be9d3fa3f92a40469e2d897&cndid=42653661&hasha=2caca015f2871a1c43772805d7b55384&hashb=f881d91faf0193265154d17f1867666ed8c88f12&hashc=e81d5f3d7c07d75bbc71cb4c6c01d535778a45f38459500286a0b5c3db33e9f6&esrc=GA_CNT_SWPS_17&utm_content=Final&utm_term=CNT_Daily", favorite: nil},
  {cocktail_name: "The Bamboo", ingredient: "1.5 oz fino sherry\n1.5 oz dry vermouth\n2 dashes Angostura bitters\n2 dashes orange bitters", direction: "1. Add all ingredients to a mixing glass.\n\n2. Stir and strain into a coupe glass.", recipe_link: "https://www.cntraveler.com/gallery/how-to-bring-japanese-cocktail-culture-to-your-home-bar?utm_source=nl&utm_brand=cnt&utm_mailing=CNT_Daily_PM_021022&utm_campaign=aud-dev&utm_medium=email&bxid=5be9d3fa3f92a40469e2d897&cndid=42653661&hasha=2caca015f2871a1c43772805d7b55384&hashb=f881d91faf0193265154d17f1867666ed8c88f12&hashc=e81d5f3d7c07d75bbc71cb4c6c01d535778a45f38459500286a0b5c3db33e9f6&esrc=GA_CNT_SWPS_17&utm_content=Final&utm_term=CNT_Daily", favorite: nil},
  {cocktail_name: "The Line", ingredient: "1/3 oz gin\n1/3 oz Benedictine\n1/3 oz sweet vermouth", direction: "1. Add all the ingredients into an ice-filled shakers\n\n2. Shake and strain into a cocktail glass", recipe_link: "https://www.cntraveler.com/gallery/how-to-bring-japanese-cocktail-culture-to-your-home-bar?utm_source=nl&utm_brand=cnt&utm_mailing=CNT_Daily_PM_021022&utm_campaign=aud-dev&utm_medium=email&bxid=5be9d3fa3f92a40469e2d897&cndid=42653661&hasha=2caca015f2871a1c43772805d7b55384&hashb=f881d91faf0193265154d17f1867666ed8c88f12&hashc=e81d5f3d7c07d75bbc71cb4c6c01d535778a45f38459500286a0b5c3db33e9f6&esrc=GA_CNT_SWPS_17&utm_content=Final&utm_term=CNT_Daily", favorite: nil},
  {cocktail_name: "Homemade Candy Cane Vodka and Candy Cane Cocktail", ingredient: "Makes 2 drinks\n\nFor Candy Cane Infused Vodka:\n1 cup Vodka\n0.5 cup Vanilla Vodka\n6 6-inch Candy Canes\n\nFor Candy Cane Cocktail:\n3 oz Candy Cane Vodka\n2 oz Crème de Cacao , clear (no color)\nDash Angostura Bitters\n2 6-inch Candy Canes", direction: "To infuse the Candy Cane Vodka:\n\n1. Pour plain vodka and vanilla vodka in a jar with a lid. Add 6 candy canes, broken up so the liquid covers them.\n\n2. Allow to sit in a cool, dark location for 3-4 days, shaking periodically to mix any sediment into liquid.\n\n3. When ready to use, strain through 2 layers of cheesecloth to trap any sediment. Seal in a jar, store in a cool, dark location until using.\n\nTo make 2 Candy Cane Cocktails:\n\n1. To make Candy Cane rimming sugar: place 2 candy canes in a chopper or blender and process into a granulated sugar. Rim cocktail glasses.\n\n2. Add Candy Cane vodka, Crème de Cacao, Bitters and ice to a shaker; shake and strain into a rimmed cocktail glass", recipe_link: "https://boulderlocavore.com/candy-cane-cocktail-with-homemade-candy-cane-infused-vodka/#wprm-recipe-container-28992", favorite: nil},
  {cocktail_name: "Blessed Thistle", ingredient: "1 oz gin (preferably Plymouth)\n1 oz Campari\n0.25 oz CioCiaro Amaro\n0.25 oz Cardamaro", direction: "1. Add all ingredients to a mixing glass.\n\n2. Add ice and stir until chilled.\n\n3. Strain over a large rock into a rocks glass.\n\n4. Garnish with an orange wheel.", recipe_link: "https://punchdrink.com/recipes/blessed-thistle/", favorite: nil},
  {cocktail_name: "Remember the Alimony", ingredient: "1.25 oz fino sherry\n1.25 oz Cynar\n0.75 oz Beefeater gin", direction: "1. Combine all the ingredients except the orange twist in a rocks glass filled with one large piece of ice.\n\n2. Stir until chilled, about 30 seconds.\n\n3. Express an orange twist over the drink and drop into the glass. ", recipe_link: "https://punchdrink.com/recipes/remember-the-alimony/", favorite: nil},
  {cocktail_name: "Smoked Old Fashioned", ingredient: "2 oz Bourbon\n0.5 oz simple syrup\n2 dashes Angostura Bitters\n2 Maraschino cherries\nGarnish: Orange Peel", direction: "1. Combine bourbon, flavored simple syrup, and bitters in cocktail shaker with ice.  \n\n2. Shake for about 20 seconds and then strain in a whiskey glass.\n\n3. Add the cherries, and then smoke the cocktail.\n\n4. Garnish with orange peel on rim or in the drink and enjoy.", recipe_link: "https://www.agedandcharred.com/blogs/recipes/smoked-old-fashioned-recipe", favorite: nil},
  {cocktail_name: "Penicillin", ingredient: "2 oz Blended Scotch\n0.75 oz Lemon Juice\n0.75 oz Ginger / Honey Syrup\n0.25 oz Islay single-malt Scotch, preferably Laphroaig 10YO\nGarnish: candied ginger", direction: "1. Add blended scotch, lemon juice and syrup to a cocktail shaker filled with ice and shake for 6 - 8 seconds.\n\n2. Strain over one large ice cube into a rocks glass.\n\n3. Gently drizzle the smoky scotch across the top.", recipe_link: "https://punchdrink.com/recipes/penicillin/", favorite: nil},
  {cocktail_name: "Rosita", ingredient: "1.5 oz reposado tequila\n0.5 oz Campari\n0.5 oz sweet vermouth\n0.5 oz dry vermouth\n1 dash Angostura Bitters", direction: "1. Add all ingredients to a mixing glass with ice.\n\n2. Stir for 5 - 10 seconds, if using small ice; 25 - 30 seconds, if using large ice.\n\n3. Strain into a rocks glass over ice, or up in a couple glass.\n\n4. Garnish with a grapefruit peel.", recipe_link: "https://robbreport.com/food-drink/spirits/best-rosita-recipe-tequila-negroni-variation-1234662098/amp/", favorite: nil},
  {cocktail_name: "Dos Hombres Hot Cider", ingredient: "1.5 cup Dos Hombres Mezcal\n1 Gallon Apple Cider\n1 Tablespoon Whole Allspice\n1 Tablespoon Whole Cloves\n1 Whole Orange (cut into slices)\n1-inch Ginger (cut into slices)\n3 Cinnamon Sticks\n4 Star Anise", direction: "1. Add all ingredients (except Mezcal) into a pot. Turn heat on low until warmed (about 30min). \n\n2. Add Dos Hombres Mezcal and serve.", recipe_link: "https://www.doshombres.com/blogs/recipes/dos-hombres-hot-cider#", favorite: nil},
  {cocktail_name: "Between the Sheets", ingredient: "1 oz white rum\n1 oz Cognac\n1 oz Cointreau (or triple sec)\n0.75 oz fresh lemon juice\nLemon peel for garnish", direction: "1. Add the rum, Cognac, Cointreau, and lemon juice to a cocktail shaker. Fill with a handful of ice and shake until very cold.\n\n2. Strain the drink into a cocktail glass. Garnish with a lemon peel.", recipe_link: "https://www.acouplecooks.com/between-the-sheets-cocktail/", favorite: nil},
  {cocktail_name: "Death to El Gusano", ingredient: "1.5 oz Dos Hombres Mezcal\n1 oz Jalapeño/Rosemary Simple Syrup\n2 Dashes Teapot Bitters\n0.75 oz Lime Juice", direction: "1. Add all ingredients to your shaker and vigorously shake.\n\n2. Serve in a rocks glass.\n\n\nTo make the simple syrup: \n1. add 1/2 cup sugar, 1/2 cup water, two Jalapeños split or quartered, and a 4-inch sprig of fresh Rosemary.\n\n2. Bring to a boil (be careful boiling), set aside for approximately 24 hours.\n\n3. Strain once there is a bit of heat from jalapeño. ", recipe_link: "https://www.doshombres.com/blogs/recipes/death-to-el-gusano#", favorite: nil},
  {cocktail_name: "Oaxacan Dead", ingredient: "1.25 oz Dos Hombres Mezcal\n1 oz Grapefruit juice\n1 oz Pomegranate juice\n0.5 oz Falernum", direction: "1. Add all ingredients into a shaker with ice. \n\n2. Shake and strain into a large rocks glass (filled with 1 x 1 Hoshizaki ice cubes).\n\n3. Top with a dash of powdered cinnamon.", recipe_link: "https://www.doshombres.com/blogs/recipes/oaxacan-dead#", favorite: nil},
  {cocktail_name: "Paloma Negra", ingredient: "1 oz Mr Black Cold Brew Liqueur\n1 oz Ojo de Tigre mezcal\nSquirt\nSqueeze of lime", direction: "1. Salt rim of a highball glass and combine ingredients in a glass over ice.\n\n2. Top with Squirt and garnish with a grapefruit wedge.", recipe_link: "https://www.insidehook.com/article/booze/paloma-recipes/amp", favorite: nil},
  {cocktail_name: "Continental Sour", ingredient: "2 oz Bourbon\n1 oz Lemon juice\n0.5 oz Rich simple syrup\n1 pcs Egg white\n0.5 oz Tawny Port", direction: "1. Add Bourbon, lemon juice, simple syrup, and egg white to your cocktail shaker. Shake it intensively for about 15 - 20 seconds to create a solid egg white foam.\n\n2. Open the shaker and fill it up with ice. Now shake again for 10 - 15 seconds.\n\n3. Open the shaker and double strain it into a chilled old fashioned glass. \n\n4. Float it with port wine.", recipe_link: "https://cocktail-society.com/recipes/whiskey-cocktails/continental-sour/", favorite: nil},
  {cocktail_name: "Wild Eye Rose", ingredient: "2 oz Irish whiskey\n0.75 oz lime juice\n0.75 oz grenadine", direction: "1. Add all ingredients to a shaker with ice and shake for 8 to 10 seconds.\n\n2. Strain into a rocks glass filled with ice, or up into a coupe glass.", recipe_link: "https://robbreport.com/food-drink/spirits/best-wild-eyed-rose-cocktail-how-to-mix-irish-whiskey-1234666932/amp/", favorite: nil},
  {cocktail_name: "El Tucan (Paloma)", ingredient: "1.5 oz blanco tequila\n0.75 oz grapefruit juice\n0.5 oz lime juice \n2 slices cucumber\n4 basil leaves\n1 tbsp simple syrup\n2 oz Grapefruit Jarritos", direction: "1. Shake ingredients (except Jarritos) with ice.\n\n2. Strain into a Collins glass, top with Jarritos and garnish with a lime wheel.", recipe_link: "https://www.insidehook.com/article/booze/paloma-recipes/amp", favorite: nil},
  {cocktail_name: "Dos Hombres Negroni", ingredient: "1.5 oz Dos Hombres Mezcal\n1 Smoked Orange Wheel\n0.25 oz Simple Syrup\n1 oz Campari\n2 Dashes Cayenne Pepper\n0.75 oz Sweet Vermouth", direction: "1. Add all ingredients into a shaker with ice.\n\n2. Shake and strain into a rocks glass.", recipe_link: "https://www.doshombres.com/blogs/recipes/negroni#", favorite: nil},
  {cocktail_name: "Oaxacan Coffee", ingredient: "1.5 oz Dos Hombres Mezcal\n0.25 oz Demerara Syrup\n1 oz Cold Brew\n3 Dashes Chocolate Mole Bitters\n0.75 oz Amaro", direction: "1. Add ingredients to a shaker filled with ice.\n\n2. Shake and strain into a rocks glass over a large ice cube.\n\n3. Garnish with expressed orange peel.", recipe_link: "https://www.doshombres.com/blogs/recipes/oaxacan-coffee#", favorite: nil},
  {cocktail_name: "Under the Volcano", ingredient: "1.5 oz Dos Hombres Mezcal\n0.5 oz Lemon juice\n2 Dashes Teapot bitters\n0.75 oz Coconut Water\n0.75 oz Pineapple Shrub\nGarnish: Pineapple leaves", direction: "1. Shake all ingredients in a shaker.\n\n2. Strain into a rocks glass.\n\nPineapple Shrub recipe: https://www.liquor.com/pineapple-shrub-recipe-5077965", recipe_link: "https://www.doshombres.com/blogs/recipes/under-the-volcano#", favorite: nil},
  {cocktail_name: "Lemon Chuhai", ingredient: "2 oz Shochu\n4 oz club soda\n0.5 oz lemon juice", direction: "1. Add all ingredients to a highball glass with ice.\n\n2. Stir to combine.", recipe_link: "https://www.cntraveler.com/gallery/how-to-bring-japanese-cocktail-culture-to-your-home-bar?utm_source=nl&utm_brand=cnt&utm_mailing=CNT_Daily_PM_021022&utm_campaign=aud-dev&utm_medium=email&bxid=5be9d3fa3f92a40469e2d897&cndid=42653661&hasha=2caca015f2871a1c43772805d7b55384&hashb=f881d91faf0193265154d17f1867666ed8c88f12&hashc=e81d5f3d7c07d75bbc71cb4c6c01d535778a45f38459500286a0b5c3db33e9f6&esrc=GA_CNT_SWPS_17&utm_content=Final&utm_term=CNT_Daily", favorite: nil},
  {cocktail_name: "Jack Frost", ingredient: "Light corn syrup or honey for rimming glasses\nFlaked coconut for rimming glasses\n8 cups ice (more or less depending on desired consistency)\n1 cup pineapple juice\n0.5 cup blue curacao\n0.5 cup vodka or light rum (I used vodka)\n0.5 cup cream of coconut", direction: "1. Rim the glasses: Pour a thin layer of corn syrup onto a plate and dip the rims into the corn syrup, then dip in coconut flakes.\n\n2. In a blender, blend ice, pineapple juice, blue curacao, vodka and cream of coconut until desired consistency is reached. Pour into glasses and serve immediately.", recipe_link: "https://theblondcook.com/jack-frost-cocktail/", favorite: nil},
  {cocktail_name: "The Million Dollar Cocktail", ingredient: "1.5 oz gin\n0.5 sweet vermouth\n0.5 oz pineapple juice\n0.5 oz egg white (~half an egg)\n0.25 oz grenadine", direction: "1. Add all ingredients to a shaker without ice and shake hard to emulsify the egg white.\n\n2. Add ice, shake again and strain into a couple glass.", recipe_link: "https://www.cntraveler.com/gallery/how-to-bring-japanese-cocktail-culture-to-your-home-bar?utm_source=nl&utm_brand=cnt&utm_mailing=CNT_Daily_PM_021022&utm_campaign=aud-dev&utm_medium=email&bxid=5be9d3fa3f92a40469e2d897&cndid=42653661&hasha=2caca015f2871a1c43772805d7b55384&hashb=f881d91faf0193265154d17f1867666ed8c88f12&hashc=e81d5f3d7c07d75bbc71cb4c6c01d535778a45f38459500286a0b5c3db33e9f6&esrc=GA_CNT_SWPS_17&utm_content=Final&utm_term=CNT_Daily", favorite: nil},
  {cocktail_name: "Harvey Wallbanger", ingredient: "2 oz Vodka\n4 oz Orange juice\n0.25 oz Galliano\nGarnish: Orange slice", direction: "1. Add all the ingredients to a tall glass and add ice.\n\n2. Stir and garnish with an orange slice.", recipe_link: "https://www.insider.com/guides/kitchen/vodka-cocktails?amp", favorite: nil},
  {cocktail_name: "Spicy Paloma", ingredient: "3 oz silver tequila\n1 grapefruit, juiced\n2 limes, juiced\n1 oz simple syrup\nlime flavored soda water\n1 teaspoon smoked chili powder, for the rim\n2 tablespoons sugar, for the rim", direction: "1. Rim the glasses: Mix together the sugar and chili powder on a small flat dish. Run a lime wedge around the edge of each glass and dip into the sugar spice mixture.\n\n2. Make the cocktail: Combine grapefruit juice, lime juice, simple syrup, and tequila in an ice filled cocktail shaker, shake well.\n\n3. Pour the mixture through the strainer into an ice-filled glass and top with lime flavored soda water.\n\n4. Garnish with lime wedges or grapefruit wedges.", recipe_link: "https://boxwoodavenue.com/spicy-paloma-cocktail/", favorite: nil},
  {cocktail_name: "Django Reinhardt", ingredient: "3 oz dry white vermouth\n0.75 oz fresh lemon juice\n0.75 oz simple syrup (see note)\n3 orange slices", direction: "1. Combine the vermouth, lemon juice, simple syrup, and 2 of the orange slices in a cocktail shaker and muddle to break down the orange. Add ice cubes to fill the shaker three-quarters full, cover, and shake to chill, about 15 seconds.\n\n2. Fill a rocks glass with ice cubes and strain the cocktail over the ice. Garnish with the remaining orange slice.", recipe_link: "https://www.epicurious.com/recipes/food/views/django-reinhardt-dry-vermouth-cocktail-kat-odell", favorite: nil},
  {cocktail_name: "Espresso Martini", ingredient: "2 oz vodka\n0.5 oz coffee liqueur (usually Kahlúa)\n1 oz espresso, freshly brewed (or cold brew concentrate)\n0.5 oz simple syrup\nGarnish: coffee beans", direction: "1. Add vodka, coffee liqueur, espresso and simple syrup to a shaker filled with ice and shake until well-chilled.\n\n2. Strain into a chilled cocktail glass.\n\n3. Garnish with 3 coffee beans.", recipe_link: "https://www.liquor.com/recipes/espresso-martini-2/", favorite: nil},
  {cocktail_name: "Maple Whiskey Sour", ingredient: "2 oz whiskey\n1 egg white\n1 oz freshly-squeezed lemon juice\n0.75 oz maple syrup\n2–3 shakes Angostura bitters (optional)", direction: "1. Add whiskey and egg white to a cocktail shaker and vigorously shake for 15-20 seconds until the egg white is very frothy.  \n\n2. Add the lemon juice, maple syrup, bitters and a handful of ice to the shaker, and shake for a few seconds to combine and chill the drink.\n\n3. Strain and serve immediately in a glass over ice.", recipe_link: "https://www.gimmesomeoven.com/maple-whiskey-sour-recipe/", favorite: nil},
  {cocktail_name: "Blood and Sand", ingredient: "1 oz blended Scotch\n1 oz cherry liqueur (preferably Cherry Heering)\n1 oz sweet vermouth\n1 oz orange juice, freshly squeezed", direction: "1. Add all ingredients to a cocktail shaker with ice and shake.\n\n2. Strain into a chilled coupe or cocktail glass.", recipe_link: "https://punchdrink.com/recipes/blood-and-sand/", favorite: nil},
  {cocktail_name: "Bill Brooks’ Godfather", ingredient: "1.5 oz blended Scotch, preferably Monkey Shoulder\n0.5 oz amaretto, preferably Vecchio\n0.25 oz palo cortado sherry, preferably Lustau\n1 dash orange bitters", direction: "1. Combine all ingredients in a mixing glass over ice and stir until chilled.\n\n2. Strain into a rocks glass over a large ice cube.\n\n3. Garnish with an orange peel and a sprinkle of bee pollen.", recipe_link: "https://punchdrink.com/recipes/godfather/", favorite: nil},
  {cocktail_name: "Bobby Heugel’s Rusty Nail", ingredient: "1 oz Johnny Walker Black\n0.25 oz Glenmorangie 10 Year\n0.25 oz Highland Park 12 Year\n0.75 oz Drambuie\n1 dash Angostura Bitters", direction: "1. Combine all ingredients in a mixing glass over ice and stir until chilled.\n\n2. Strain into a rocks glass over a large ice cube.\n\n3. Garnish with a lemon twist.", recipe_link: "https://punchdrink.com/recipes/bobby-heugels-rusty-nail/", favorite: nil},
  {cocktail_name: "Morning Glory Fizz", ingredient: "2 oz blended Scotch\n0.5 oz lemon juice\n0.5 oz lime juice\n0.5 oz simple syrup (1:1, sugar:water)\n1 egg white\n3-4 dashes absinthe\nsoda, to top\nGarnish: orange peel or orange wheel", direction: "1. Add all ingredients to a cocktail shaker and shake without ice.\n\n2. Add ice and shake until well-chilled.\n\n3. Pour into a cocktail glass and top up with soda.\n\n4. Garnish with an orange peel or orange wheel.", recipe_link: "https://punchdrink.com/recipes/morning-glory-fizz/", favorite: nil},
  {cocktail_name: "Aperol Flip", ingredient: "1.5 oz gin\n1 frozen egg white cube (about 1 ounce)\n0.75 oz lemon juice\n0.5 oz Aperol\n2 teaspoons granulated sugar\n3 dashes orange bitters\npinch kosher salt\n1 cup ice cubes", direction: "1. Combine gin, egg white, lemon juice, Aperol, sugar, bitters, and salt in cocktail shaker and vigorously shake until egg white has melted and drink is very foamy, 60 to 90 seconds.\n\n2. Add ice to cocktail shaker and vigorously shake for 30 seconds. Strain into chilled coupe or rocks glass and serve.", recipe_link: "https://www.americastestkitchen.com/cooksillustrated/recipes/9141-aperol-flip?pac=p8CYCPiyb1Tuyrf8TGigZRk2pjIOkvkk7/ZholowAsg=%0A&extcode=NSCIL16IG&utm_source=instagram&utm_medium=photo&utm_content=flip&utm_campaign=ciholiday", favorite: nil},
  {cocktail_name: "Holiday Negroni", ingredient: "1 oz Campari\n1 oz London dry gin (like Beefeater)\n1 oz sweet vermouth\n3 juniper berries\n1 slice orange", direction: "1. Combine ingredients in an old fashioned glass filled with ice.\n\n2. Stir and garnish with an orange slice and whole or cracked Juniper berries.", recipe_link: "https://huckberry.com/journal/posts/hranek-negroni?tag=journal&type=content&subtype=drink&utm_source=Standard%20-%20Newsletter%20-%20Americas&utm_medium=email&utm_campaign=12.21.22_Rundown-Anthony%20%2801GMRCYJTVBXDD7F3HRH8EY27S%29&term=blast&_kx=mt43Ao3OYuT_2VDqXYbFVIAWCSLAbe9cyHil8cyNf58%3D.bVvvBe", favorite: nil},
  {cocktail_name: "Planter's Punch", ingredient: "2 oz gold rum\n1 oz fresh lime juice\n0.5 oz simple syrup\n2 dashes Angostura bitters\nSplash of grenadine on top (optional)", direction: "1. Add ingredients into a cocktail shaker with ice and shake well. Strain into a highball glass filled with crushed ice. \n\n2. Garnish with mint springs and/or whatever tropical fruit you have on hand.", recipe_link: "https://huckberry.com/journal/posts/goldfarb-planters-punch", favorite: nil},
  {cocktail_name: "Cranberry Orange Whiskey Sour", ingredient: "Makes 2 drinks\n\n0.5 cup cranberry juice\n4 oz whiskey\n0.25 cups simple syrup\n0.25 cups freshly squeezed orange juice\nJuice of 1 lime\n4 oz ginger beer", direction: "1. Fill a cocktail shaker with ice and add cranberry juice, whiskey, simple syrup, orange, and lime juice. Shake until well chilled. \n\n2. Fill two glasses with ice and strain mixture into glasses. Top off with ginger beer to serve. ", recipe_link: "https://12ft.io/proxy?q=https%3A%2F%2Fwww.delish.com%2Fcooking%2Frecipe-ideas%2Fa38412186%2Fcranberry-orange-whiskey-sour-recipe%2F", favorite: nil},
  {cocktail_name: "Peppermint Martini", ingredient: "Makes 3 drinks\n\n0.5 cups semisweet chocolate chips, melted\n0.25 cups crushed candy canes\n6 oz white chocolate liqueur\n4 oz vodka\n2 oz peppermint schnapps\n2 tbsp. heavy cream\nIce\n3 small candy canes", direction: "1. Pour melted chocolate onto a shallow plate and place crushed candy canes on another shallow plate. Dip rims of martini glasses first in chocolate, then in candy canes.\n\n2. Pour remaining chocolate into the bottom of glasses and swirl to coat the bottom ⅓ of glasses with chocolate. Place glasses in freezer while you prepare the drink.\n\n3. Combine white chocolate liqueur, vodka, peppermint schnapps, and heavy cream in a cocktail shaker. Fill to the top with ice and shake until cold.\n\n4. Divide drink among 3 glasses and garnish with a candy cane before serving.", recipe_link: "https://12ft.io/proxy?q=https%3A%2F%2Fwww.delish.com%2Fholiday-recipes%2Fchristmas%2Fa25415480%2Fpeppermintinis-recipe%2F", favorite: nil},
  {cocktail_name: "Ginger-Cranberry Whiskey Sour", ingredient: "GINGER SIMPLE SYRUP\n0.5 cups granulated sugar\n0.5 cups water\n1 (3\") piece ginger, peeled\n\nDRINK\n2 oz bourbon\n1 oz ginger simple syrup\n1 oz unsweetened cranberry juice\nIce\nMandarin orange or clementine peel, for serving", direction: "GINGER SIMPLE SYRUP\n1. In a 16-ounce or larger jar with a lid or resealable container, combine granulated sugar and water. Using a microplane or fine grater, grate ginger into jar. Cover and shake jar until sugar has dissolved, 3 to 4 minutes.\n\n2. Using a fine-mesh sieve, strain ginger mixture into a small bowl, pressing with the back of a spoon to expel as much liquid as possible. Discard solids.\n\n3. Make Ahead: Ginger simple syrup can be made 3 weeks ahead. Store in an airtight container and refrigerate.\n\nDRINK\n1. In a cocktail shaker, combine bourbon, ginger syrup, and cranberry juice. Fill shaker with ice and vigorously shake until outside of shaker is very frosty, about 20 seconds.\n\n2. Strain into a rocks glass or coupe. Garnish glass with orange peel.", recipe_link: "https://12ft.io/proxy?q=https%3A%2F%2Fwww.delish.com%2Fcooking%2Frecipe-ideas%2Fa41910191%2Fginger-cranberry-whiskey-sour-recipe%2F", favorite: nil},
  {cocktail_name: "Prosecco Mint Julep", ingredient: "6 mint leaves, plus more for serving\n0.75 oz simple syrup\n1 oz bourbon\nCrushed ice\n3 oz dry prosecco\n1 dash of Angostura bitters", direction: "1. In a mint julep cup or highball glass, muddle mint leaves and simple syrup with a muddler or the end of a wooden spoon handle until mint is dark green and has lost its shape.\n\n2. Add bourbon, then ice, prosecco, and bitters, and gently stir to combine. Garnish with more mint.", recipe_link: "https://12ft.io/proxy?q=https%3A%2F%2Fwww.delish.com%2Fholiday-recipes%2Fchristmas%2Fa42004813%2Fprosecco-mint-julep-recipe%2F", favorite: nil},
  {cocktail_name: "Airmail", ingredient: "1 oz rum\n0.5 oz lime juice\n0.5 oz honey syrup (2:1)\n2 to 3 oz. sparkling wine", direction: "1. Pour rum, lime and honey syrup into an ice-filled shaker and shake for 6 - 10 seconds.\n\n2. Add sparkling wine and strain into a tall glass over ice.\n\n3. Optional: Garnish with a lime wheel", recipe_link: "https://robbreport.com/food-drink/spirits/best-champagne-cocktails-new-years-eve-1234790144/amp/", favorite: nil},
  {cocktail_name: "Champagne Cocktail", ingredient: "1 sugar cube\n1-2 dashes of bitters\n5-6 oz Prosecco, or other sparkling wine", direction: "1. Saturate the sugar cube with bitters in a coupe glass.\n\n2. Fill the coupe with champagne or sparkling wine and garnish with a lemon peel.", recipe_link: "https://robbreport.com/food-drink/spirits/best-champagne-cocktails-new-years-eve-1234790144/amp/", favorite: nil},
  {cocktail_name: "Division Bell", ingredient: "1 oz mezcal, preferably Del Maguey Vida\n0.75 oz Aperol\n0.5 oz maraschino liqueur\n0.75 oz lime juice\nGarnish: grapefruit twist, expressed and discarded", direction: "1. Combine all ingredients in a cocktail shaker with ice and shake until chilled.\n\n2. Strain into a coupe.\n\n3. Express a grapefruit twist over the surface of the drink, then discard.", recipe_link: "https://punchdrink.com/recipes/division-bell/", favorite: nil},
  {cocktail_name: "Bourbon Toscano", ingredient: "2.5 oz Kentucky straight bourbon\n.5 oz Aperol\n2 dashes Addition rosemary cocktail spice bitters\nSmoked ice cubes*\nGarnish: dehydrated rosemary sprig\nGarnish: dehydrated orange wheel", direction: "1. Add all ingredients into a mixing glass with ice and stir until well-chilled.\n\n2. Strain into an Old Fashioned glass over the smoked ice cubes.\n\n3. Garnish with a dehydrated rosemary sprig and dehydrated orange wheel.\n\n*Smoked ice cubes: Fill a smoker with cherrywood chips (or add a smoker box filled with cherrywood chips to a grill). Fill metal containers with water, set inside the smoker or grill, and close the lid. Smoke for 30 minutes or until desired flavor is achieved. Freeze the smoked water in ice cube trays.", recipe_link: "https://www.liquor.com/recipes/bourbon-toscano/", favorite: nil},
  {cocktail_name: "Rum and Smoke", ingredient: "1.5 oz aged rum\n0.75 oz Oloroso sherry\n0.25 oz Tempus Fugit Gran Classico bitter liqueur\n1 teaspoon smoked rosemary simple syrup*\nGarnish: flamed rosemary sprig", direction: "1. In a mixing glass, add the rum, sherry, Gran Classico and smoked rosemary simple syrup with ice and stir until well-chilled.\n\n2. Strain into a rocks glass over fresh ice.\n\n3. Garnish with a flamed rosemary sprig.\n\n4. Cover with a glass dome and let sit for 1 to 2 minutes, or until the cocktail is well smoked, before drinking.\n\n\n*Smoked-rosemary simple syrup: In a saucepan over medium heat, combine 1/2 cup sugar, 1/2 cup water and the zest from 1 orange and stir until just under a boil. Remove from the heat. With a kitchen torch or lighter, flame one rosemary sprig until it begins to smoke on all sides. Drop the smoking sprig into the saucepan and let steep, covered, for 20 minutes. Strain out the solids and store the syrup in the refrigerator up to one week.", recipe_link: "https://www.liquor.com/recipes/rum-and-smoke/", favorite: nil},
  {cocktail_name: "Velvet Old Fashioned", ingredient: "2 oz blended Scotch\n0.25 oz Pedro Ximenez sherry\n0.25 oz Demerara syrup\n2 dashes Angostura bitters", direction: "1. Stir all ingredients with ice and strain into an ice-filled glass.", recipe_link: "https://www.diffordsguide.com/cocktails/recipe/3539/velvet-old-fashioned", favorite: nil},
  {cocktail_name: "Sherry Cobler", ingredient: "1.5 oz Pedro Ximenez Sherry \n0.5 oz Bourbon Whiskey \n1/3 oz Lemon Juice \n1/3 oz Simple Syrup ", direction: "1. Add sliced citrus to your glass.\n\n2. Fill your glass with crushed ice.\n\n3. Add all the ingredients & stir.\n\n4. Garnish with a slice of orange, mint and berries (or seasonal fruit).", recipe_link: "https://www.cocktailexplorer.co/cocktails/sherry-cobbler/steve-the-bartender/", favorite: nil},
  {cocktail_name: "Hot Lips", ingredient: "0.75 oz jalapeño-infused tequila (see below)\n0.75 oz mezcal, preferably Sombra\n0.5 oz lemon juice\n0.5 oz pineapple juice\n0.5 oz vanilla syrup (see below)\n1 teaspoon Petite Canne sugar cane syrup\nGarnish: salt rim", direction: "1. Combine all ingredients in a mixing glass with ice and stir until chilled.\n\n2. Strain into a rocks glass filled with ice.\n\n------\n\nJalapeño-Infused Tequila\n5 medium jalapeños, diced\n750 ml blanco tequila\n\nSteep the jalapeños and tequila in a container for 5 minutes. Taste the mixture to ensure that the spice level is to your taste. Allow to steep longer for a spicier end product. Strain out the jalapeños when the desired spice level has been reached. Store in an airtight container, refrigerated, indefinitely.\n\nVanilla Syrup\n1 cup water\n1 cup sugar\nhttps://fountainavenuekitchen.com/simple-vanilla-syrup/\n\nBring the water and sugar to a boil in a medium saucepan over medium high heat. Continue to cook at a rapid simmer until the mixture thickens slightly, about 8-9 minutes.\n\nRemove the pan from the heat, stir in the vanilla, and cool to room temperature. Transfer to a glass jar or bottle and refrigerate. The syrup will last for two weeks or more. Eventually, the sugar will begin to crystalize on the side of the jar or bottle, but the taste will likely still be fine.", recipe_link: "https://punchdrink.com/recipes/hot-lips/", favorite: nil},
  {cocktail_name: "Milano-Torino Sour", ingredient: "1 oz Campari\n1 oz dry vermouth, preferably LoFi\n0.75 oz grapefruit juice\n0.5 oz lime juice\n0.5 oz simple syrup\n1 egg white\n2 dashes orange bitters, preferably Regans’", direction: "1. Combine all ingredients in a cocktail shaker and dry shake without ice.\n\n2. Add ice and shake again until chilled.\n\n3. Strain into a coupe.", recipe_link: "https://punchdrink.com/recipes/milano-torino-sour/", favorite: nil},
  {cocktail_name: "Manolito's Swampwater", ingredient: "2.5 oz green Chartreuse\n0.75 oz pineapple juice\n0.75 oz lime juice\n2 basil leaves\n15 oz ice (by weight)\n1 ounce dry rum, such as Don Q or Flor de Caña ", direction: "1. Combine all ingredients but the rum in a blender, and blend until smooth.\n\n2. Pour into a jar and float rum atop.\n\n3. Garnish with mint.", recipe_link: "https://punchdrink.com/recipes/manolitos-swampwater/", favorite: nil},
  {cocktail_name: "Ned King's Gem", ingredient: "1 oz Cognac, preferably Pierre Ferrand 1840\n1 oz Jamaica rum, preferably Appleton Signature\n0.75 oz lime juice\n0.5 oz simple pineapple syrup (see below)\n1 dash Angostura bitters\nGarnish: freshly grated cinnamon", direction: "1. Combine all ingredients in a shaker.\n\n2. Add ice and shake until chilled.\n\n3. Strain into a Nick & Nora or other stemmed cocktail glass.\n\n4. Grate cinnamon over the top of the drink.\n\n-----\n\nSimple 2:1 Pineapple Syrup\n1 cup fresh pineapple juice, not from concentrate\n2 cups cane sugar\n\nStir juice and sugar to combine in a saucepan over medium-low heat, stirring frequently until all sugar is dissolved. Allow to cool, transfer to an airtight container and refrigerate.", recipe_link: "https://punchdrink.com/recipes/ned-kings-gem/", favorite: nil},
  {cocktail_name: "Smoke Break", ingredient: "1 pinch cherrywood chips\n2 oz bourbon\n0.75 oz cream sherry\n0.5 oz Carpano Antica Formula vermouth\n2 dashes Woodford Reserve spiced cherry bourbon-barrel-aged bitters\n1 dash Angostura bitters\nGarnish: dark chocolate\nGarnish: dried black cherries", direction: "1. Using a smoking gun and the cherrywood chips, add smoke to a glass decanter, then cover the top of the decanter to capture the smoke.\n\n2. Add the bourbon, cream sherry, vermouth, both bitters and ice into a mixing glass, and stir until well-chilled.\n\n3. Strain the contents into the prepared decanter, then cap the decanter so the drink mingles with the smoke. Let sit until desired smokiness is reached.\n\n4. Pour into a rocks glass over one large ice cube.\n\n5. Serve with a tray of dark chocolate and dried black cherries on the side.", recipe_link: "https://www.liquor.com/recipes/smoke-break/", favorite: nil},
  {cocktail_name: "Tootsie Roll", ingredient: "2 oz Rye Whiskey \n1 oz Pedro Ximenez Sherry \n6 dashes Chocolate Bitters ", direction: "1. Combine all ingredients into your mixing glass.\n\n2. Stir for 30-40 seconds.\n\n3. Strain into a chilled cocktail glass.\n\n4. Garnish with a maraschino cherry.", recipe_link: "https://www.cocktailexplorer.co/cocktails/tootsie-roll/steve-the-bartender/", favorite: nil}
])
